import { useAuth0 } from "@auth0/auth0-react";

const AuthForm = () => {
  const { loginWithRedirect } = useAuth0();

  const handleLogin = () => {
    loginWithRedirect(); // Redirects to the Auth0 login page
  };

  return (
    <main className="h-screen flex flex-col justify-between relative">
      <div className="flex-grow flex items-center justify-center">
        <div className="p-8 max-w-md w-full">
          <button
            className="w-full bg-black text-white py-2 mt-4"
            onClick={handleLogin} // Users can choose social login on the universal login page
          >
            Log In / Sign Up
          </button>
        </div>
      </div>
    </main>
  );
};

export default AuthForm;
