import React, { useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";

const AuthForm: React.FC = () => {
  const { loginWithRedirect } = useAuth0(); // Keep this for social login fallback if needed
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // Replace with your own backend endpoint if needed
      const response = await fetch(
        `https://${process.env.REACT_APP_AUTH0_DOMAIN}/oauth/token`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            grant_type: "password",
            username: email,
            password: password,
            client_id: process.env.REACT_APP_AUTH0_CLIENT_ID,
            audience: process.env.REACT_APP_AUTH0_AUDIENCE,
          }),
        }
      );

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem("accessToken", data.access_token);
        // Handle further steps like redirecting or setting user state
      } else {
        console.error("Login failed");
      }
    } catch (error) {
      console.error("Error logging in:", error);
    }
  };

  return (
    <main className="h-screen flex flex-col justify-between relative">
      <div className="flex-grow flex items-center justify-center">
        <div className="p-8 max-w-md w-full">
          <div className="text-center lg:text-body-lg mt-4 flex pb-2">
            {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
            <span
              onClick={() => setIsLogin(!isLogin)}
              className="text-blue-500 cursor-pointer px-2"
            >
              {isLogin ? "Sign up" : "Log in"}
            </span>
          </div>
          <form className="space-y-4" onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="w-full px-4 py-2 border border-gray-300"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-4 py-2 border border-gray-300"
            />
            <button
              type="submit"
              className="w-full bg-black text-white py-2 lg:button-lg"
            >
              Log In
            </button>
          </form>
        </div>
      </div>
    </main>
  );
};

export default AuthForm;
