import React from "react";

const Tweets: React.FC = () => {
  // Function to truncate text
  const truncateText = (text: string): string => {
    let maxLength = 13;
    return text.length > maxLength ? text.slice(0, maxLength) + "..." : text;
  };

  // Sample text for demonstration
  const tweetText =
    "Top 5 dog-themed costumes that are trending this Halloween";

  return (
    <div className="bg-green-300 w-full h-full">
      {/* Top row showing replies and count */}
      <div className="bg-slate-500 flex flex-row justify-between  p-4">
        <div>replies</div>
        <div>64</div>
      </div>

      {/* Tweet content */}
      <div className="flex flex-row justify-between items-start bg-slate-500  p-4">
        <div className="bg-purple-600 p-2 flex-grow">
          {truncateText(tweetText)}
        </div>
        <div className="flex flex-col items-end ml-2">
          <div>â†’</div>
          <div>1h ago</div>
        </div>
      </div>
    </div>
  );
};

export default Tweets;
