import React, { FC } from "react";
import Payment from "./Payment";

interface PurchaseProps {
  onClose: () => void;
}

const Purchase: FC<PurchaseProps> = ({ onClose }) => {
  const handleBackgroundClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (
      e.target instanceof HTMLDivElement &&
      e.target.id === "purchase-overlay"
    ) {
      onClose();
    }
  };

  return (
    <div
      id="purchase-overlay"
      className="fixed inset-0 bg-gray-800 bg-opacity-75 flex justify-center items-center z-50"
      onClick={handleBackgroundClick}
    >
      <div className="bg-white p-16 w-full l:w-[40%] xl:w-[35%] space-y-4 relative">
        <button
          className="absolute top-2 right-2 text-xl font-bold text-gray-500 hover:text-gray-700"
          onClick={onClose}
        >
          Ã—
        </button>
        <h2 className="text-2xl font-semibold mb-4">Purchase Credits</h2>
        <p>This is the purchase pop-up content. Add more details here.</p>
      </div>
    </div>
  );
};

export default Purchase;
