import React from "react";
import tweetsMock from "../mockdata/tweets.json"; // Adjust the path as necessary
import Arrow from "../assets/icon/arrow.svg";
import { MdRefresh } from "react-icons/md"; // Example icon, replace as needed

const Tweets: React.FC = () => {
  // Function to truncate text
  const truncateText = (text: string): string => {
    let maxLength = 200;
    return text.length > maxLength ? text.slice(0, maxLength) + "..." : text;
  };

  // Function to handle refresh (dummy function for demonstration)
  const handleRefresh = () => {
    console.log("Refreshed!");
  };

  // Filter tweets for userId 1
  const userId = 1;
  const userTweets = tweetsMock.filter((tweet) => tweet.userId === userId);

  return (
    <div className="w-full relative">
      {/* Top row showing replies and count */}
      <div className="flex flex-row justify-between p-4 border-2 border-black relative">
        <div className="font-bold p-2">Replies</div>
        <div className="font-bold">{userTweets.length}</div>{" "}
        {/* Display the dynamic count */}
        {/* Refresh button positioned at the top right */}
        <button
          onClick={handleRefresh}
          className="absolute top-0 right-0 mt-2 mr-2 bg-gray-100 p-1 rounded hover:bg-gray-200"
        >
          <MdRefresh size={20} />
        </button>
      </div>

      <div className="border-l-2 border-r-2 border-black">
        {userTweets.map((tweet) => (
          <div
            key={tweet.id}
            onClick={() => window.open(tweet.link, "_blank")}
            className="flex flex-row justify-between items-start p-4 cursor-pointer hover:bg-black hover:text-white border-b-2 border-black transition-colors"
          >
            <div className="p-2 flex-grow">{truncateText(tweet.text)}</div>
            <div className="flex flex-col items-end ml-2">
              <img src={Arrow} alt="arrow icon" />
              <div className="text-time-lg">
                {new Date(tweet.timestamp).toLocaleTimeString([], {
                  hour: "2-digit",
                  minute: "2-digit",
                })}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tweets;
