import React, { useState, useEffect } from "react";
import userMock from "../mockdata/user.json";
import Smile from "../assets/images/smile.png";

const Prompt = () => {
  const [prompt, setPrompt] = useState("");

  // Load the prompt from user.json when the component mounts
  useEffect(() => {
    setPrompt(userMock.prompt); // Assumes user.json has a `prompt` field
  }, []);

  return (
    <div>
      <div className="bg-gray-200 w-full p-1 pb-12 relative">
        <textarea
          className="w-full h-32 p-4 resize-none bg-gray-100"
          placeholder="Enter your prompt here..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)} // Allows user to edit the prompt
        ></textarea>
        <div className="w-full p-2 flex flex-row items-start">
          <div className=" h-12 w-12 flex-shrink-0">
            <img src={Smile} alt="smile icons" />
          </div>
          <div className="bg-gray-200 px-2 ml-2 flex-grow">
            <p>
              This is a template response that will appear when Run Test is
              clicked so that users can test different responses.
            </p>
          </div>
        </div>
        <div className="absolute bottom-2 right-2 flex space-x-2">
          <button className="flex items-center bg-blue-500 text-white px-4 py-2 rounded">
            <span className="material-icons mr-2">Run</span>
            Test
          </button>
          <button className="bg-gray-500 text-white px-4 py-2 rounded">
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default Prompt;
