import React, { useState, useEffect } from "react";
import userMock from "../mockdata/user.json";
import Refresh from "../assets/icon/refresh.svg";
import Magic from "../assets/icon/magic.svg";

const Prompt = () => {
  const [prompt, setPrompt] = useState("");

  // Load the initial prompt from user.json when the component mounts
  useEffect(() => {
    setPrompt(userMock.prompt); // Assumes user.json has a `prompt` field
  }, []);

  // Handler to reload the initial prompt
  const handleReloadPrompt = () => {
    setPrompt(userMock.prompt);
  };

  return (
    <div>
      <div className="bg-gray-200 w-full p-1 pb-12 relative">
        <h1>Prompt</h1>
        <div className="relative">
          <textarea
            className="w-full h-32 p-4 resize-none bg-gray-100"
            placeholder="Enter your prompt here..."
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)} // Allows user to edit the prompt
          ></textarea>
          <button
            className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
            onClick={handleReloadPrompt}
          >
            <img src={Refresh} alt="refresh button" />
          </button>
        </div>
        <div className="w-full p-2 flex flex-col items-start">
          <h2 className="font-bold pb-2">Test response</h2>
          <p className="pb-8">
            This is a template response that will appear when Run Test is
            clicked so that users can test different responses.
          </p>
        </div>
        <div className="absolute bottom-2 right-2 flex space-x-2">
          <button className="flex items-center bg-gray-500 text-white px-4 space-x-2 py-2 ">
            <img src={Magic} alt="Magic load svg" />
            <p>Test</p>
          </button>
          <button className="bg-gray-500 text-white px-4 py-2 ">Save</button>
        </div>
      </div>
    </div>
  );
};

export default Prompt;
