import React, { useState, useEffect } from "react";
import userMock from "../mockdata/user.json"; // Adjust the path as necessary

const Prompt = () => {
  const [prompt, setPrompt] = useState("");

  // Load the prompt from user.json when the component mounts
  useEffect(() => {
    setPrompt(userMock.prompt); // Assumes user.json has a `prompt` field
  }, []);

  return (
    <div>
      <div className="bg-green-300 w-full p-1 pb-12 relative">
        <textarea
          className="w-full h-32 p-4 resize-none  border-gray-300 rounded"
          placeholder="Enter your prompt here..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)} // Allows user to edit the prompt
        ></textarea>
        <div className="absolute bottom-2 right-2 flex space-x-2">
          <button className="flex items-center bg-blue-500 text-white px-4 py-2 rounded">
            <span className="material-icons mr-2">send</span>
            Test
          </button>
          <button className="bg-gray-500 text-white px-4 py-2 rounded">
            Save
          </button>
        </div>
      </div>
      <div className="bg-pink-300 w-full p-2 flex flex-row items-start">
        <div className="bg-yellow-400 h-12 w-12 flex-shrink-0">pic</div>
        <div className="bg-slate-500 p-2 ml-2 flex-grow">
          <p>
            response response response response response response response
            response response response response
          </p>
        </div>
      </div>
    </div>
  );
};

export default Prompt;
