import React, { useState, useEffect } from "react";

const LoginPage = () => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and register
  const [userLoggedIn, setUserLoggedIn] = useState(false); // Simulates user authentication state

  useEffect(() => {
    // Replace with actual authentication check logic
    const token = localStorage.getItem("authToken");
    setUserLoggedIn(!!token);
  }, []);

  const handleLogin = () => {
    // Implement login logic and set userLoggedIn to true upon success
    localStorage.setItem("authToken", "dummyToken");
    setUserLoggedIn(true);
  };

  const handleRegister = () => {
    // Implement registration logic
    localStorage.setItem("authToken", "dummyToken");
    setUserLoggedIn(true);
  };

  if (userLoggedIn) {
    return null; // If the user is logged in, don't show the login page
  }

  return (
    <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex justify-center items-center z-50">
      <div className="bg-white p-6 w-11/12 md:w-1/3 rounded-md shadow-lg">
        <button
          className="absolute top-2 right-2 text-lg font-bold text-gray-500 hover:text-gray-700"
          onClick={() => setUserLoggedIn(true)} // Simulates closing
        >
          Ã—
        </button>
        <h2 className="text-2xl font-semibold mb-4">
          {isLogin ? "Login" : "Register"}
        </h2>
        {isLogin ? (
          <div>
            <input
              type="text"
              placeholder="Username"
              className="w-full mb-2 p-2 border border-gray-300 rounded"
            />
            <input
              type="password"
              placeholder="Password"
              className="w-full mb-4 p-2 border border-gray-300 rounded"
            />
            <button
              className="w-full bg-blue-500 text-white py-2 rounded"
              onClick={handleLogin}
            >
              Login
            </button>
          </div>
        ) : (
          <div>
            <input
              type="text"
              placeholder="Username"
              className="w-full mb-2 p-2 border border-gray-300 rounded"
            />
            <input
              type="email"
              placeholder="Email"
              className="w-full mb-2 p-2 border border-gray-300 rounded"
            />
            <input
              type="password"
              placeholder="Password"
              className="w-full mb-4 p-2 border border-gray-300 rounded"
            />
            <button
              className="w-full bg-green-500 text-white py-2 rounded"
              onClick={handleRegister}
            >
              Register
            </button>
          </div>
        )}
        <p
          className="mt-4 text-center text-blue-600 cursor-pointer"
          onClick={() => setIsLogin(!isLogin)}
        >
          {isLogin
            ? "Don't have an account? Register"
            : "Already have an account? Login"}
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
