"use client";

import React, { useEffect } from "react";
import Auth0Lock from "auth0-lock";
import { useNavigate } from "react-router-dom";

const Login: React.FC = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const lock = new Auth0Lock(
      "oUlsTOudb2Z0DLs6rYcPgdRyrUuWqqvY", // Your Client ID
      "dev-da2463l8g0mvkpdx.us.auth0.com", // Your Domain
      {
        auth: {
          redirectUrl: window.location.origin,
          responseType: "token id_token",
          params: {
            scope: "openid profile email", // Correct way to include scope
          },
        },
        theme: {
          primaryColor: "#000000", // Customize this color to match your design
        },
        languageDictionary: {
          title: "Log In",
        },
        closable: false, // Prevents the widget from being closed
      }
    );

    // Event listener for authenticated event
    lock.on("authenticated", (authResult) => {
      lock.getUserInfo(authResult.accessToken, (error, profile) => {
        if (!error) {
          // Save user information if needed
          // Redirect the user to the dashboard
          navigate("/dashboard");
        }
      });
    });

    // Show the widget when the component mounts
    lock.show();
  }, [navigate]);

  return (
    <div
      id="auth0-lock-container"
      className="flex justify-center items-center h-screen"
    ></div>
  );
};

export default Login;
