import React, { useState } from "react";
import { HelioCheckout } from "@heliofi/checkout-react";

const initialConfig = {
  paylinkId: "672e903ed76f84523f383539", // Default ID for $10
  theme: { themeMode: "light" },
};

const Payment = () => {
  const [helioConfig, setHelioConfig] = useState(initialConfig);

  const handleButtonClick = (newPaylinkId: string) => {
    setHelioConfig((prevConfig) => ({
      ...prevConfig,
      paylinkId: newPaylinkId,
    }));
  };

  return (
    <div className="p-4">
      <div className="flex space-x-4 mb-4">
        <button
          className="px-4 py-2 bg-blue-500 text-white rounded"
          onClick={() => handleButtonClick("672e903ed76f84523f383539")}
        >
          $10
        </button>
        <button
          className="px-4 py-2 bg-green-500 text-white rounded"
          onClick={() => handleButtonClick("abcdef1234567890")}
        >
          $50
        </button>
        <button
          className="px-4 py-2 bg-red-500 text-white rounded"
          onClick={() => handleButtonClick("1234567890abcdef")}
        >
          $100
        </button>
      </div>
      <HelioCheckout config={helioConfig} />
    </div>
  );
};

export default Payment;
